import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [role, setRole] = useState('patient');
    const [message, setMessage] = useState('');
    const [isUsernameAvailable, setIsUsernameAvailable] = useState(true);

    const checkUsernameAvailability = async () => {
        try {
            const response = await axios.post('http://localhost:8080/api/auth/check-username', { username });
            setIsUsernameAvailable(response.data.available);
            setMessage(response.data.available ? '' : 'Username is already taken.');
        } catch (error) {
            setMessage('Error checking username.');
        }
    };

    const validatePassword = () => {
        const regex = /^(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
        return regex.test(password);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validatePassword()) {
            setMessage('Password must contain at least 8 characters, including uppercase and lowercase letters.');
            return;
        }

        if (password !== confirmPassword) {
            setMessage('Passwords do not match.');
            return;
        }

        if (!isUsernameAvailable) {
            setMessage('Username is already taken.');
            return;
        }

        try {
            await axios.post('http://localhost:8080/api/auth/register', {
                username,
                password,
                role
            });
            setMessage('Registration successful!');
        } catch (error) {
            setMessage('Error registering user.');
        }
    };

    return (
        <div className="register-container">
            <h2>Register</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        onBlur={checkUsernameAvailability}
                        required
                    />
                </div>
                <div>
                    <label>Password</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Confirm Password</label>
                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Role</label>
                    <select value={role} onChange={(e) => setRole(e.target.value)}>
                        <option value="patient">Patient</option>
                        <option value="dermatologist">Dermatologist</option>
                    </select>
                </div>
                <button type="submit">Register</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default Register;
